mapper.insert:

	select nvl(max(member_id), 0)+1  as member_id  
	from tb_member

Insert into TB_MEMBER 
(
	member_id
	,user_id
	,password
	,user_name
	,email
	,phone
	,nickname
	,zipcode
	,address1
	,address2
	,regdate
) values (
	#{member_id}
	,#{user_id}
	,#{password}
	,#{user_name}
	,#{email}
	,#{phone}
	,#{nickname}
	,#{zipcode}
	,#{address1}
	,#{address2}
	,sysdate
)
